INSERT INTO `activity` (`id`, `title`, `remark`, `mediaId`, `mediaType`, `content`, `length`, `fromCourseId`, `fromCourseSetId`, `fromUserId`, `startTime`, `endTime`, `createdTime`, `updatedTime`, `copyId`, `migrateLessonId`, `finishType`, `finishData`) VALUES (1,'如何创建课程？',NULL,1,'text','<p><span style=\"font-size:14px;\">1、教师身份</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">路径：网站首页【我的教学】-【创建课程】。</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">2、管理员身份</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">路径：【管理后台】-【课程】-【课程管理】-【创建课程】，只有管理员有权限进入管理后台。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n',0,1,1,1,0,0,1516008405,0,0,0,'time','1'),(2,'如何设置课程教师？',NULL,2,'text','<p>路径：【课程管理页】-【默认计划设置】-【教师设置】。</p>\r\n\r\n<p>说明：只能添加有教师权限的用户为课程老师；</p>\r\n\r\n<p><span style=\"font-size:14px;\"><strong>如何给某个用户教师权限？</strong></span></p>\r\n\r\n<p>路径：【管理后台】-【用户】-【用户管理】，搜索到要提升权限的用户，点击操作一栏下的【设置用户组】，勾选对应权限保存即可。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008430,0,0,0,'time','1'),(3,'如何上传视频让学员学习？',NULL,3,'text','<p><span style=\"font-size:16px;\">路径：【课程管理页】-【默认计划设置】-【课时管理】-【添加课时】-【选择教学手段&middot;视频】。</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>教师上传视频&mdash;&mdash;教师发布课时&mdash;&mdash;教师发布课程&mdash;&mdash;学员加入课程&mdash;&mdash;学员观看视频</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"/files/course/2019/01-11/145857120de4781404.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008459,0,0,0,'time','1'),(4,'如何添加作业练习和考试？',NULL,4,'text','<p><strong>考试功能说明：<a href=\"http://www.qiqiuyu.com/faq/784/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/784/detail</a></strong></p>\r\n\r\n<p><strong>作业功能说明：<a href=\"http://www.qiqiuyu.com/faq/785/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/785/detail</a></strong></p>\r\n\r\n<p><strong>练习功能说明：<a href=\"http://www.qiqiuyu.com/faq/786/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/786/detail</a></strong></p>\r\n\r\n<p><strong>考试、作业、练习的区别：<a href=\"http://www.qiqiuyu.com/faq/751/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/751/detail</a></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/files/course/2019/01-11/14582845b3c1548340.png\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/files/course/2019/01-11/150534e8930a874658.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008485,0,0,0,'time','1'),(5,'如何添加学习资料让学员下载？',NULL,5,'text','<p>路径：【课程管理页】-【默认计划设置】-【课时管理】-【添加课时】-【选择教学任务&middot;下载资料】，上传资料并保存。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>确保课程已发布，学员加入课程后，可在课程【资料区】中下载资料。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n',0,1,1,1,0,0,1516008522,0,0,0,'time','1'),(6,'管理员如何管理课程和设置教师权限？',NULL,6,'text','<p><strong>1、管理课程</strong></p>\r\n\r\n<p>查看和管理网站所有课程，路径：【管理后台】-【课程】-【课程管理】。</p>\r\n\r\n<p><strong>2、设置教师权限</strong></p>\r\n\r\n<p>如果需要限制教师的权限，比如不允许教师设置课程价格，不允许教师添加或移除课程学员，设置路径：</p>\r\n\r\n<p>【管理后台】-【系统】-【课程管理】-【课程】-【权限与版权】。</p>\r\n\r\n<p>说明：ES目前有管理员的权限分配系统，但教师的详细权限分配暂未实现。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008543,0,0,0,'time','1'),(7,'如何查看课程订单和营收？',NULL,7,'text','<p><strong>1、查看课程订单</strong></p>\r\n\r\n<p>（1）学员，【账户中心】-【我的订单】可以查看自己的课程订单。</p>\r\n\r\n<p>（2）教师/管理员/超管，【课程管理页】-【默认计划设置】-【订单统计】可以查看购买该课程的学员订单；前提：超级管理员开启了允许教师查看课程订单，【管理后台】-【系统】-【课程设置】-【课程】中开启。</p>\r\n\r\n<p>（3）管理员/超管，【管理后台】-【交易】-【用户订单】中查看所有用户购买课程的订单。</p>\r\n\r\n<p><strong>2、查看营收</strong></p>\r\n\r\n<p>（1）查看网站整体营收，路径【管理后台】-【交易】-【网校账单】，查看网校净收入/收入/支出；还可根据起始时间/支付方式/类型等查看网站收入。</p>\r\n\r\n<p>（2）查看单个课程的营收，【课程管理页】-【默认计划设置】-【订单统计】可导出订单。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008568,0,0,0,'time','1'),(8,'关闭课程与删除课程',NULL,8,'text','<p>关闭课程与删除课程路径都在网站后台，需管理员以上权限才能操作，教师无权操作。</p>\r\n\r\n<p><strong>关闭课程：</strong></p>\r\n\r\n<p>路径：【管理后台】-【课程】-【课程管理】-【关闭课程】；</p>\r\n\r\n<p>只有已发布的课程，才有关闭的概念，关闭后还可以再次发布。</p>\r\n\r\n<p>关闭后，未加入课程的学员访问将提示课程未发布无法加入；已经加入课程的学员，可以继续学习课程内容，如果不希望学员继续学习，可在课程的学员管理中移除学员。</p>\r\n\r\n<p><strong>删除课程：</strong></p>\r\n\r\n<p>路径：【管理后台】-【课程】-【课程管理】-【删除课程】；</p>\r\n\r\n<p>删除后与课程相关的学习记录都将无法恢复，学员无法访问课程，<span style=\"color:#e74c3c;\"><strong>删除操作不可逆</strong></span>，请慎重操作，删除时需要输入登陆密码再次确认。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1516008595,0,0,0,'time','1'),(9,'班级、课程、教学计划、任务（课时）的关系',NULL,9,'text','<p><strong>班级＞课程＞教学计划＞课时/任务</strong></p>\r\n\r\n<p><span style=\"color:#e74c3c;\"><strong>一个班级可以包含多个课程，一个课程可以包含十个教学计划，一个教学计划可以包含300个课时。</strong></span></p>\r\n\r\n<p><strong>1、课时/任务</strong></p>\r\n\r\n<p>v8.3.0之前，叫任务；v8.3.0之后，叫课时；即任务=课时;</p>\r\n\r\n<p>课时/任务有视频、音频、直播、图文、PPT、Word、PDF等类型，是学员学习的内容；课时/任务不能单独售卖；</p>\r\n\r\n<p><strong>2、教学计划</strong></p>\r\n\r\n<p>由课时/任务组成，一个教学计划最多可以添加300个任务/课时，可以理解为一个计划里最多放300个学习视频；教学计划可以单独售卖；</p>\r\n\r\n<p><strong>3、课程</strong></p>\r\n\r\n<p>由教学计划组成，一个课程可添加最多10个教学计划，每个教学计划都可以设置价格，单独购买；</p>\r\n\r\n<p><strong>4、班级</strong></p>\r\n\r\n<p>由课程组成，一个班级可以添加多个课程，班级可以理解为课程包或者系列课程集合；</p>\r\n\r\n<p>购买班级后可以学习班级内的所有课程，购买后再新增课程到班级，已购买班级的学员可以免费学习新增的课程；</p>\r\n\r\n<p>目前仅支持添加只有一个教学计划的课程到班级，也就是如果一个课程有多个教学计划，不能添加到班级。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1547190435,0,0,0,'time','1'),(10,'课程类型介绍：普通课程、直播课程、录播公开课、直播公开课',NULL,10,'text','<p>ES系统里，有四种课程类型：</p>\r\n\r\n<p><strong>1、普通课程</strong></p>\r\n\r\n<p>支持添加多个视频（云视频、本地视频、第三方视频）、直播、音频、图文、PPT、Flash、文档、资料下载课时，支持设置价格，开放购买。</p>\r\n\r\n<p>学员必须要加入课程（收费课程需购买，免费课程可直接加入），才能学习。</p>\r\n\r\n<p><strong>2、直播课程（需先购买直播人数）</strong></p>\r\n\r\n<p>支持添加多个直播课时，各课时间直播时间不能有冲突，支持设置加入人数限制，设置价格，开放购买。</p>\r\n\r\n<p>学员必须要加入课程（收费课程需购买，免费课程可直接加入），才能学习。</p>\r\n\r\n<p><strong>3、录播公开课</strong></p>\r\n\r\n<p>只能添加一个视频（云视频、本地视频、第三方视频）课时，没有价格设置，任何人无论有没有网校账号，都可以直接学习此公开课，适用于推广试学课程。</p>\r\n\r\n<p><strong>4、直播公开课（需先购买直播人数）</strong></p>\r\n\r\n<p>只能添加一个直播课时，没有价格设置，任何人无论有没有网校账号，直播开始前可以设置开始提醒，直播开始后都可以直接学习此公开课，适用于推广试学课程。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>【注意】：直播课程和直播公开课需要购买直播人数后才能使用，购买直播链接：<strong><a href=\"http://open.edusoho.com/buy/cloud/live\">http://open.edusoho.com/buy/cloud/live</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><span style=\"color:#1abc9c;\"><strong>普通课程 与 直播课程 的区别：</strong></span></span></p>\r\n\r\n<p>1）普通课程里可以添加直播课时，但是直播课程里不能添加音频、图文、文档等课时类型；</p>\r\n\r\n<p>2）直播课程图片上有&ldquo;直播&rdquo;标签，普通课程里即使有直播课时，也不会显示直播标签。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#1abc9c;\"><span style=\"font-size:16px;\"><strong>普通课程/直播课程 与 录播/直播公开课 的区别：</strong></span></span></p>\r\n\r\n<p><strong>1）价格</strong></p>\r\n\r\n<p>普通课程/直播课程有价格设置，公开课没有价格设置，完全是无门槛观看，打开地址即可；</p>\r\n\r\n<p><strong>2）课时数</strong></p>\r\n\r\n<p>普通课程/直播课程都可添加多个课时，公开课都只支持一个课时。</p>\r\n\r\n<p><img alt=\"\" src=\"/files/course/2019/01-11/1521139a8089278662.png\" /></p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1547190480,0,0,0,'time','1'),(11,'课程价格与加入设置',NULL,11,'text','<p>1、价格：默认为0.00元，显示为&ldquo;免费&rdquo;；</p>\r\n\r\n<p>2、可加入设置：</p>\r\n\r\n<ul>\r\n	<li>可加入：学员可付费购买课程或直接加入免费课程；</li>\r\n	<li>不可加入：隐藏课程购买和加入按钮，只能通过设置会员价格，学员通过会员身份加入或者教师手动添加学员到课程；</li>\r\n</ul>\r\n\r\n<p>3、加入截止日期：</p>\r\n\r\n<ul>\r\n	<li>不限时间：只要课程发布，学员即可购买，除非管理员手动关闭课程；</li>\r\n	<li>自定义：可选择一个具体日期，例如2018年10月4日，4日23:59:59后，学员不能购买加入课程；</li>\r\n</ul>\r\n\r\n<p>4、会员免费学课程：</p>\r\n\r\n<ul>\r\n	<li>需先安装&ldquo;会员专区&rdquo;插件，到【管理后台】-【系统】-【用户设置】-【会员设置】中设置会员等级，才能设置会员免费学课程；</li>\r\n	<li>会员功能仅支持商业版使用，开源版暂不支持。</li>\r\n</ul>\r\n\r\n<p>5、网站必须申请收款接口并成功配置到后台，学员才能购买课程；目前支持支付宝、微信、网银收款，申请教程请看：<strong><a href=\"http://www.qiqiuyu.com/faq/257/detail\">http://www.qiqiuyu.com/faq/257/detail</a></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/files/course/2019/01-11/151138adf3aa288536.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1547190702,0,0,0,'time','1'),(12,'系统功能学习说明（网站管理员）',NULL,12,'text','<p><span style=\"font-size:18px;\"><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>【必学】管理后台功能说明：<a href=\"http://www.qiqiuyu.com/course/429\">http://www.qiqiuyu.com/course/429</a></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>【常用】系统功能问答中心（搜关键词找教程）：<a href=\"http://www.qiqiuyu.com/faq/search\">http://www.qiqiuyu.com/faq/search</a></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n',0,1,1,1,0,0,1547191162,0,0,0,'time','1');
