INSERT INTO `course_draft` (`id`, `title`, `summary`, `courseId`, `content`, `userId`, `activityId`, `createdTime`) VALUES (24,'',NULL,1,'<p><span style=\"font-size:14px;\">1、教师身份</span></p>\n\n<p><span style=\"font-size:14px;\">路径：网站首页【我的教学】-【创建课程】。</span></p>\n\n<p><span style=\"font-size:14px;\">2、管理员身份</span></p>\n\n<p><span style=\"font-size:14px;\">路径：【管理后台】-【课程】-【课程管理】-【创建课程】，只有管理员有权限进入管理后台。</span></p>\n\n<p>&nbsp;</p>\n\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\n',1,1,1547190930),(25,'',NULL,1,'<p>路径：【课程管理页】-【默认计划设置】-【教师设置】。</p>\n\n<p>说明：只能添加有教师权限的用户为课程老师；</p>\n\n<p><span style=\"font-size:14px;\"><strong>如何给某个用户教师权限？</strong></span></p>\n\n<p>路径：【管理后台】-【用户】-【用户管理】，搜索到要提升权限的用户，点击操作一栏下的【设置用户组】，勾选对应权限保存即可。</p>\n\n<p>&nbsp;</p>\n\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\n\n<p>&nbsp;</p>\n',1,2,1547190935),(28,'',NULL,1,'<p><strong>考试功能说明：<a href=\"http://www.qiqiuyu.com/faq/784/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/784/detail</a></strong></p>\n\n<p><strong>作业功能说明：<a href=\"http://www.qiqiuyu.com/faq/785/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/785/detail</a></strong></p>\n\n<p><strong>练习功能说明：<a href=\"http://www.qiqiuyu.com/faq/786/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/786/detail</a></strong></p>\n\n<p><strong>考试、作业、练习的区别：<a href=\"http://www.qiqiuyu.com/faq/751/detail\" target=\"_blank\">http://www.qiqiuyu.com/faq/751/detail</a></strong></p>\n\n<p><img alt=\"\" src=\"/files/course/2019/01-11/14582845b3c1548340.png\" /></p>\n\n<p><img alt=\"\" src=\"/files/course/2019/01-11/150534e8930a874658.png\" /></p>\n\n<p>&nbsp;</p>\n',1,4,1547190961),(30,'',NULL,1,'<p>路径：【课程管理页】-【默认计划设置】-【课时管理】-【添加课时】-【选择教学任务&middot;下载资料】，上传资料并保存。</p>\n\n<p>&nbsp;</p>\n\n<p>确保课程已发布，学员加入课程后，可在课程【资料区】中下载资料。</p>\n\n<p>&nbsp;</p>\n\n<p><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></p>\n',1,5,1547190974),(32,'',NULL,1,'<p><span style=\"font-size:16px;\"><strong>【必学】课程功能说明：<a href=\"http://www.qiqiuyu.com/course/22\">http://www.qiqiuyu.com/course/22</a></strong></span></p>\n\n<p><span style=\"font-size:16px;\"><strong>【必学】管理后台功能说明：<a href=\"http://www.qiqiuyu.com/course/429\">http://www.qiqiuyu.com/course/429</a></strong></span></p>\n\n<p><span style=\"font-size:16px;\"><strong>【常用】系统功能问答中心（搜关键词找教程）：<a href=\"http://www.qiqiuyu.com/faq/search\">http://www.qiqiuyu.com/faq/search</a></strong></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n',1,0,1547191163);
